                int t=1ll*e[j-l]*a[j+i]%P;
                a[j+i]=a[j]-t,Mod2(a[j+i]);
                a[j]+=t,Mod1(a[j]);
            }
        }
    }
    if(f==-1) {
        ll Inv=1ll*I[n]*J[n-1]%P;
        rep(i,0,n-1) a[i]=a[i]*Inv%P;
    }
}

V operator * (V a,V b){
    if(!a.size() || !b.size()) return { };
    int n=a.size()+b.size()-1,R=Init(n);
    a.resize(R),b.resize(R);
