void Ins(const V &S){
	int now=0;
	for(int i:S) {
		int &nxt=trie[now][i];
		if(!nxt) nxt=++cnt;
		now=nxt;
	}
	mk[now]=1;
}
void Build(){
	static queue <int> que;
	rep(i,0,1) if(trie[0][i]) que.push(trie[0][i]);
