typedef vector <int> V;
int trie[M][2],fail[M],mk[M],cnt;
void Ins(const V &S){
	int now=0;
	for(int i:S) {
		int &nxt=trie[now][i];
		if(!nxt) nxt=++cnt;
		now=nxt;
	}
	mk[now]=1;
}
void Build(){
	static queue <int> que;
	rep(i,0,1) if(trie[0][i]) que.push(trie[0][i]);
	while(!que.empty()) { 
		int u=que.front(); que.pop();
		mk[u]|=mk[fail[u]];
		rep(i,0,1){
			int &v=trie[u][i];
			if(v) que.push(v);
			(!v?v:fail[v])=trie[fail[u]][i];
		}
	}
	mk[cnt+1]=1;
	rep(i,0,cnt) rep(j,0,1) if(mk[i] || mk[trie[i][j]]) trie[i][j]=cnt+1;
}

V Read(){
	V Res;
	rep(i,1,rd()) Res.pb(rd());
	return Res;
}

vector <Pii> G[N];
ll dis[N][M][M];
struct Node{
	int u,s,t;
	ll d;
	bool operator < (const Node &__) const {
		return d>__.d;
	}
};
priority_queue <Node> que;
void Upd(int u,int s,int t,ll d){
	if(mk[s]||mk[t]||dis[u][s][t]<=d) return;
	dis[u][s][t]=d,que.push((Node){u,s,t,d});
}

int main(){
	c=n=rd()-1,m=rd(),k=rd();
	++c; // 建立一个空虚点
	rep(i,1,m) {
