	Build();
	memset(dis,255,sizeof dis);
	rep(i,0,cnt) if(!mk[i]) dis[n+1][i][i]=0; // 单位矩阵
	rep(u,0,1) rep(i,0,cnt) Upd(u,i,trie[i][u],1);
	while(!que.empty()) {
