void Sieve_Multiplicative_Function(int n){
    F[1]=1;
    for(int i=2;i<=n;++i){
        if(!notprime[i]) {
            prime[++primecnt]=i;
            for(ll j=i;j<=n;j*=i) F[j]=CalcG(j),D[j]=j;
            // 计算F(p_i^t)
        }
        for(int j=1;j<=primecnt && 1ll*i*prime[j]<=n;++j) {
            notprime[i*prime[j]]=1;
            int k=i*prime[j];
            if(i%prime[j]==0) {
                D[k]=D[i] * prime[j];
                F[k]=F[i/D[i]] * F[D[k]];
                break;
            }
            D[k]=prime[j];
            F[k]=F[i] * F[prime[j]];
        }
    }
}

