    }
    w[0]=Cp(1,0);
    rep(i,1,29) w[i]=w[i-1]*Cp(cos(2*Pi/3),sin(2*Pi/3));
    // 复平面3阶单位根

    n=rd(),m=rd();
    rep(i,1,n) x.a[Count(rd())]++;
    for(R=1;R<=ma;R*=3);

