		rep(j,1,f) {
			int x=rd();
			if(i<=n) x+=n;
			E[i][x]=1;
		}
		rep(j,1,m) {
			int f=(j-1)*D+1;
			rep(k,0,D-1) G[i][j]|=E[i][f+k]<<k;
			if(G[i][j]) AddEdge(i,j);
		}
