					if(a==R || b==R || i==T) { ans[a][b]+=dp[i][a][b][j]; continue; }
					db t=dp[i][a][b][j]/sz[j];
					for(reg int k=1;k<=m;k+=4) {
						tmp[k][G[j][k]]+=t;
