        dp[i][j]=1e9;
        rep(k,max(G[i-1][j],1),i) {
            int x=dp[k-1][j-1]+dp[i-k][j]+i+1;
            if(x<=dp[i][j]) G[i][j]=k,dp[i][j]=x;
            else break;
        }
    }
    while(~scanf("%d%d",&n,&m)) printf("%.6lf\n",1.0*dp[n][min(m,10)]/(n+1));
