        memset(dp,0,sizeof dp);
        drep(i,n,1) rep(j,i,n) rep(k,i,j) 
            rep(d,~a[k]?a[k]:1,~a[k]?a[k]:n) 
                dp[i][j][d]=(dp[i][j][d]+1ll*(i<k?dp[i][k-1][d]:1)*(k<j?dp[k+1][j][d+1]:1)%P*C[j-i][k-i])%P;
        printf("%d\n",dp[1][n][1]);
    }
}




