		while(l<=r) {
			int mid=(l+r)>>1;
			if(A[mid].b<A[i].a) l=mid+1,res=mid;
			else r=mid-1;
		}
		L[i]=res;
		if(i-1>L[i]) Q[++qc]=(Query){A[i].b+1,i-1,i,1},Q[++qc]=(Query){A[i].b+1,L[i],i,-1};
	}
	sort(Q+1,Q+qc+1);
	int p=1;
	rep(i,0,n) {
		if(i) Add(A[i].a,1);
		while(p<=qc && Q[p].p<=i) Ans[Q[p].id]+=Q[p].k*Que(Q[p].x),p++;
	}
	rep(i,1,n) dp[i]=max(dp[i-1],dp[L[i]]+1+Ans[i]);
	int ans=dp[n];
	printf("%d\n",ans);
