ll qpow(ll x,ll k=P-2){
	ll res=1;
	for(;k;k>>=1,x=x*x%P) if(k&1) res=res*x%P;
	return res;
}
int Phi[N+1],notpri[N+1],pri[N/4],pc;

void Exp(int *a){
	static int b[N];
	rep(i,1,n) b[i-1]=1ll*a[i]*i%P;
