#define drep(i,a,b) for(int i=a,i##end=b;i>=i##end;--i)

template <class T> void cmin(T &a, T b){ ((a>b)&&(a=b)); }
template <class T> void cmax(T &a, T b){ ((a<b)&&(a=b)); }


