		while(j && s[i]!=s[j+1]) j=nxt[j];
		if(s[i]==s[j+1]) ++j;
		nxt[i]=j;
		cmax(Max[i],Max[j]);
	}
	rep(i,0,n) {
		if(i<n) Trans[i][s[i+1]-'a']=i+1;
		rep(j,0,25) if(j!=s[i+1]-'a') Trans[i][j]=Trans[nxt[i]][j];
	}

	int ans=0;	
	st[0][0]++;
	rep(i,1,n) {
		int j=i,f=1;
		rep(k,1,m) {
			st[i][k]+=25-(Max[j]-'a'),j=Trans[j][Max[j]-'a'];
			if(!j) {
				st[i][k]++,f=0;
				break;
			}
		}
		if(j==i) ans+=f;
	}

	dp[0][0]=1;
	rep(i,1,m) {
		rep(j,0,n) if(dp[i-1][j]) {
			dp[i][Trans[j][Max[j]-'a']]+=dp[i-1][j],Mod1(dp[i][Trans[j][Max[j]-'a']]);
			dp[i][0]=(dp[i][0]+1ll*(25-(Max[j]-'a'))*dp[i-1][j])%P;
		}
	}

	rep(i,0,n) { // 枚举初始状态
		rep(j,0,m) if(st[i][j] && dp[m-j][i]) { // 枚举走了几步第一次到0
			ans=(ans+1ll*st[i][j]*dp[m-j][i])%P;
		}
	}
	ans=(qpow(26,m)-ans+P)%P;
	printf("%d\n",ans);
}










```

# 「CodePlus 2017 11 月赛」Yazid 的新生舞会              

最基本的分析这里只保留：$cnt>\frac{len}{2}\Rightarrow 2cnt>len$

对于每一个合法的区间，合法的众数显然只有一个

考虑对于每一个众数计算答案，把$x$出现的位置拿出来成一个序列$A_i$

如果选择的区间恰好包含$A_i,A_{i+1},\cdots ,A_j$，那么合法的情况就是$2(j-i+1)>R-L+1,L\in[A_{i-1}+1,A_i],R\in[A_{j},A_{j},A_{j+1}-1]$

参数分离得到$2j-R>2i-L$

如果对于每一个$L$更新答案，那么更新的是一段区间，不妨设其为$UL,UR$

对于每个$R$查询则是一段前缀 和 的区间 

我们知道树状数组维护区间修改区间查询需要做一次差分，而这次是区间前缀和

也就是说是再高一维。。

不妨在$UL$上加,$UR$上减，那么在$p$处的更新对于在$k$处的查询的贡献是

$\cfrac{(k-p+1)(k-p+2)}{2}=\cfrac{k^2+p^2-2pk+3k-3p+2}{2}$

那么直接处理这个式子即可，需要维护$updval,p\cdot updval,p^2\cdot updval$

查询时加入$k$的贡献

```cpp
#include<bits/stdc++.h>
using namespace std;
using ll=long long;
#define rep(i,a,b) for(int i=a,i##end=b;i<=i##end;++i)
int rd(){
	int s=0; char c;
	while(c=getchar(),c<48);
	do s=s*10+c-'0';
	while(c=getchar(),c>47);
	return s;
}
enum{N=1000010};
int n;
vector <int> A[N];
ll ans,s1[N],s2[N],s3[N];
void Add(int p,ll x) {
	p--; ll a=x,b=x*p,c=x*p*p;
	for(p+=n+1;p<=n*2;p+=p&-p) s1[p]+=a,s2[p]+=b,s3[p]+=c;
}
void Add(int l,int r,int x) { Add(l,x),Add(r+1,-x); }
ll Que(int p) {
	ll r1=0,r2=0,r3=0,tp=p;
	for(p+=n;p;p-=p&-p) r1+=s1[p],r2+=s2[p],r3+=s3[p];
	return ((tp*tp+tp)*r1-(2*tp+1)*r2+r3)/2;
}
ll Que(int l,int r) { return Que(r)-Que(l-1); }
int main(){ 
	freopen("party.in","r",stdin),n=rd(),rd();
	rep(i,1,n) A[rd()].push_back(i);
	rep(k,0,n-1) if(A[k].size()) {
		rep(i,0,A[k].size()-1) {
			int p=A[k][i],l=i?A[k][i-1]:0,r=i<(int)A[k].size()-1?A[k][i+1]:n+1;
			Add(2*i-p+1,2*i-l,1);
			ans+=Que(2*(i+1)-r,2*(i+1)-p-1);
		}
		rep(i,0,A[k].size()-1) {
			int p=A[k][i],l=i?A[k][i-1]:0;
			Add(2*i-p+1,2*i-l,-1);
		}
	}
	fprintf(fopen("party.out","w"),"%lld\n",ans);
}
```

# 「FJWC2020Day5-zzq」lg

设模数为$P$

考虑对于每一个$\gcd$计算$\text{lcm}$之积$F(m)$

那么可以想到强制每个数都是$\gcd$的倍数，问题转化为求$\lfloor \frac{m}{gcd}\rfloor $以内所有$\text{lcm}$的积$G(m)$

那么对于每个质因数依次考虑,则得到一个简单的式子

$$G(m)=\begin{aligned}\prod p_i^{\sum_{j=1}m^n-(m-\lfloor \frac{m}{p_i^j}\rfloor )^n} \end{aligned}$$

其中枚举的$j$是$p_i$至少出现$j$次的方案数，枚举的$j$是$\log m$ 级别的

肯定是先求出指数$\mod \varphi(P)$，可以线性预处理出所有的$i^n \mod \varphi(P)$

对于每个$p_i$求出指数后还要快速幂，复杂度就是$O(|p_i|\log P)=O(m)$，实际带有一些常数

那么求$G(m)$的复杂度上界是$O(m\log m)$，实际上$\lfloor \frac{m}{i}\rfloor $有很多重复，复杂度要低很多

得到的每个$\gcd$的答案还要把强制取出的$\gcd$补上，是{% raw %}$\gcd^{{\lfloor \frac{m}{gcd}\rfloor }^n}${% endraw %}

那么对于$G(m)$枚举倍数进行容斥的到$F(m)$即可，需要求每个$F(m)$的逆元，复杂度是$O(m(\log P+\log m))$

总复杂度的上界就是$O(m\log P)$

```cpp
#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
#define rep(i,a,b) for(int i=a;i<=b;++i)
enum{N=200010,P=998244353,Phi=P-1};
int n,m,p[N],mk[N],g[N],ig[N],h[N];
ll qpow(ll x,ll k,ll P) {
	ll res=1;
	for(;k;k>>=1,x=x*x%P) if(k&1) res=res*x%P;
	return res;
}
int G(int m) {
	if(h[m]) return h[m];
	ll ans=1;
	rep(i,2,m) if(!mk[i]) {
		ll d=1,s=0;
		for(;(d*=i)<=m;) s+=p[m]-p[m-m/d]; // 带入上式求出答案
		ans=ans*qpow(i,(s%Phi+Phi)%Phi,P)%P;
	}
	return h[m]=ans;
}
int main(){
	freopen("lg.in","r",stdin),freopen("lg.out","w",stdout);
	scanf("%d%d",&n,&m);
	rep(i,2,N-1) if(!mk[i]) for(int j=i+i;j<N;j+=i) mk[j]=1;
	rep(i,0,m) p[i]=qpow(i,n,Phi); // 预处理出i^n mod Phi
	rep(i,1,m) g[i]=G(m/i)*qpow(i,p[m/i],P)%P; // 注意要补上
	ll ans=1;
	for(int i=m;i;i--){
		for(int j=i+i;j<=m;j+=i) g[i]=1ll*g[i]*ig[j]%P; // 容斥得到f
		ig[i]=qpow(g[i],P-2,P),ans=ans*qpow(g[i],i,P)%P;
	}
	printf("%lld\n",ans);
}



```

# 「GDOI2020模拟赛day1」Permutation              

为了便于叙述，设原题中的 $n$ 为$N$

### 题目分析

要求一个$1-n$的环排列，看成是一个环遍历

发现每条边的权值限制了遍历过程中穿过这条边的次数

取$1$为根，强制从$1$开始遍历，考虑以一个个**自由段**（即未确定前后连接关系的子段）的形式维护$u$子树中的序列段

那么，只需要满足$u$子树中自由段的个数为$\frac{w(u,fa_u)}{2}$ （每一个自由段的两端均对应一次跨越）即可

分析即可发现$w(u,fa_u)$显然是$O(size_u)$级别的，因此考虑树形背包

那么我们就需要支持合并两组自由段

$$ \ $$

### $O(N^3-N^2\log N)$

设当前$1\ldots n$个自由端，合并上$m$个自由段(从子树合并上来是恰好$m$个)

注意这些自由段之间是无序的

先考虑一个简单的模型：

把$n$个无序自由段拼接成$m$个无序自由段，设方案数为$W(n,m)$，则考虑

先把$n$个自由段排列，然后选出$n-m$个间隔连接，然后除掉得到的$m$个段之间的排列

得到$\begin{aligned} W(n,m)=\frac{n!}{m!}\binom{n-1}{n-m} \end{aligned}$

类似的，可以把$n+m$个自由段排成一排，合并成若干段

但是显然存在的问题就是：可能在两组自由段之间形成了连接，这样的连接是非法的

因此考虑容斥

$\begin{aligned} dp'_{d}\longleftarrow\sum_{i=1}^ndp_{u,i}\sum_{j=1}^idp_v (-1)^{i-j}W(i,j) \sum_{k=1}^m (-1)^{m-k}W(m-1,k)\sum_{d=1}^{j+k}W(j+k,d)\end{aligned}$

将上式分解为四步转移

$n,i\rightarrow j,O(n^2)$

$m\rightarrow k,O(m)$

$j,k\rightarrow j+k,O(nm)$

$j+k\rightarrow d,O((n+m)^2)$

其中$O(n^2,(n+m)^2)$的部分如果用卷积优化，即可做到$O(N^2\log N)$

但是$O(N^3)$就$pts75$了...



Tips:注意在将$1$号节点加入序列时，用上面的方法无法保证它在序列首

需要特殊处理，始终强制它在第一个

### $O(N^2)$

当我发现这个做法不需要任何优化就可以做到$O(N^2)$的时候。。。。

~~把这个容斥的过程爆开~~

先对于每个儿子的$dp$值按照容斥系数进行上文中$m\rightarrow k$的变换，复杂度为$O(size_u)$

然后进行背包合并，由树形背包的复杂度分析，总复杂度为$O(N^2)$

最后发现其实我们只需要知道$dp_{u,w(u,fa_u)}$，因此这里也只需要$O(size_u)$

综上，复杂度为$O(N^2)$

```cpp
#include<bits/stdc++.h>
using namespace std;

typedef long long ll;
typedef unsigned long long ull;
typedef double db;
typedef pair <int,int> Pii;
#define reg register
#define mp make_pair
#define pb push_back
#define Mod1(x) ((x>=P)&&(x-=P))
#define Mod2(x) ((x<0)&&(x+=P))
#define rep(i,a,b) for(int i=a,i##end=b;i<=i##end;++i)
#define drep(i,a,b) for(int i=a,i##end=b;i>=i##end;--i)
template <class T> inline void cmin(T& a,T b){ ((a>b)&&(a=b)); }
template <class T> inline void cmax(T& a,T b){ ((a<b)&&(a=b)); }

const int DEBUG=1;
#define Log(...) (DEBUG&&(fprintf(stderr,__VA_ARGS__)))

char IO;
int rd(){
	int s=0,f=0;
	while(!isdigit(IO=getchar())) if(IO=='-') f=1;
	do s=(s<<1)+(s<<3)+(IO^'0');
	while(isdigit(IO=getchar()));
	return f?-s:s;
}

bool Mbe;
const int N=5010;

int n,P;
ll qpow(ll x,ll k=P-2){
	ll res=1;
	for(;k;k>>=1,x=x*x%P) if(k&1) res=res*x%P;
	return res;
}
int I[N],J[N],C[N][N],W[N][N];

struct Edge{
	int to,nxt,w;
} e[N<<1];
int head[N],ecnt;
void AddEdge(int u,int v,int w){
	e[++ecnt]=(Edge){v,head[u],w};
	head[u]=ecnt;
}

int sz[N],w[N],dp[N][N],T[N];
int A[N],B[N];

void dfs(int u,int f){
	sz[u]=0,dp[u][0]=1;
	for(int i=head[u];i;i=e[i].nxt){
		int v=e[i].to;
		if(v==f) continue;
		w[v]=e[i].w,dfs(v,u);
		rep(a,0,sz[u]) T[a]=dp[u][a],dp[u][a]=0;
		rep(a,0,sz[u]) rep(b,1,sz[v]) dp[u][a+b]=(dp[u][a+b]+1ll*T[a]*dp[v][b])%P;
		sz[u]+=sz[v];
	}
	int s=0;
	rep(i,w[u],sz[u]+1) {
		int e=W[i][w[u]];
		if(u==1) e=1ll*C[i-1][i-w[u]]*J[i-1]%P*I[w[u]-1]%P;
		s=(s+1ll*e*dp[u][i-1])%P;
	}
    // 求出我们需要的点值

	rep(i,w[u]+1,sz[u]) dp[u][i]=0;
	if(!s) puts("0"),exit(0);
    // 容斥系数变换
	rep(i,1,w[u]) {
		dp[u][i]=1ll*s*W[w[u]][i]%P;
		if((w[u]-i)&1) dp[u][i]=P-dp[u][i];
	}
	sz[u]=w[u];
}

bool Med;
int main(){
	Log("Memory taken %.2lf\n",(&Med-&Mbe)/1024.0/1024.0);
	n=rd(),P=rd();
	rep(i,J[0]=1,n) J[i]=1ll*J[i-1]*i%P;
	I[n]=qpow(J[n]);
	drep(i,n,1) I[i-1]=1ll*I[i]*i%P;
	rep(i,0,n) rep(j,C[i][0]=1,i) C[i][j]=C[i-1][j]+C[i-1][j-1],Mod1(C[i][j]),W[i][j]=1ll*C[i-1][i-j]*J[i]%P*I[j]%P;
	rep(i,2,n){
		int u=rd(),v=rd(),w=rd();
		if(w&1) return puts("0");
		AddEdge(u,v,w/=2),AddEdge(v,u,w);
	}
	dfs(w[1]=1,0);
	dp[1][1]=1ll*dp[1][1]*n%P;
	printf("%d\n",dp[1][1]);
}
```

# 「GXOI / GZOI2019」宝牌一大堆

~~麻将.jpg~~

观察牌型和计算方法可知，选择一个杠与选择一个面子对于牌型的贡献是等价的

而选择一个杠的答案一定没有选择一个刻子优，因此是没有任何意义的

除去 "七对子" "国士无双" 的特殊情况后，此外的情况就是选择 4个面子 + 1个雀头

容易想到对于每个花色dp得到选择$i$个面子$j$个雀头的答案，然后背包合并

为了$dp$顺子的情况，可以存储前面两个位置作为顺子头的个数

令$dp_{i,j,a,b}$表示当前选了$i$个面子，$j$个雀头，上两个位置选了$a$个顺子，上一个位置选了$b$个顺子

暴力维护dp即可

```cpp
const int INF=1e9+10;

int min(int x,int y,int z){ return min(min(x,y),z); }

int n,m;
int C[5][5],c[4][12],mk[4][12];
ll ans,val[5][2];

void Check7(){
	static int val[40],cnt;
	cnt=0;
	rep(i,0,3) rep(j,0,9) if(c[i][j]>=2) val[++cnt]=C[c[i][j]][2]<<(mk[i][j]*2);
	if(cnt<7) return;
	sort(val+1,val+cnt+1,greater<int>());
	ll res=7;
	rep(i,1,7) res*=val[i];
	cmax(ans,res);
}

void Check13(){
	ll res=13,x=0,y=0;
	auto chk=[&](ll a,ll b) {
		if(!x) x=a,y=b;
		if(x*b<a*y) x=a,y=b;
	};
	rep(i,0,2) {
		if(!c[i][0] || !c[i][8]) return;
		res*=c[i][0]*c[i][8];
		res<<=(mk[i][0]+mk[i][8]);
		if(c[i][0]>=2) chk(C[c[i][0]][2]<<(mk[i][0]*2),c[i][0]<<mk[i][0]);
		if(c[i][8]>=2) chk(C[c[i][8]][2]<<(mk[i][8]*2),c[i][8]<<mk[i][8]);
	}
	rep(i,0,6) {
		if(!c[3][i]) return;
		res<<=mk[3][i];
		res*=c[3][i];
		if(c[3][i]>=2) chk(C[c[3][i]][2]<<(mk[3][i]*2),c[3][i]<<mk[3][i]);
	}
	if(!x) return;
	cmax(ans,res*x/y);
}

void Work(int *cnt,int *mk,ll res[5][2]){
	static ll dp[2][5][2][5][5],w[5];
	int cur=0;
	memset(dp,0,sizeof dp),dp[cur][0][0][0][0]=1;
	rep(t,0,8) {
		int x=cnt[t]; ll val;
		memset(dp[!cur],0,sizeof dp[!cur]);
		rep(i,0,x) w[i]=C[x][i]<<(mk[t]*i);
		rep(i,0,4) rep(j,0,1) rep(a,0,x) rep(b,0,x-a) if((val=dp[cur][i][j][a][b])) {
			int d=a+b,y=x-d,u=min(cnt[t+1]-b,cnt[t+2]);

			rep(k,0,min(3-i,y-3,u))
				cmax(dp[!cur][i+k+1][j][b][k],val*w[d+k+3]); // 刻 + 顺

			rep(k,0,min(4-i,y,u))
				cmax(dp[!cur][i+k][j][b][k],val*w[d+k]); // 顺

			if(j) continue;
			rep(k,0,min(4-i,y-2,u))
				cmax(dp[!cur][i+k][j+1][b][k],val*w[d+k+2]); // 雀 + 顺
		}
		cur^=1;
	}
	drep(i,4,0) drep(j,1,0) if(res[i][j]) {
		rep(a,0,4-i) rep(b,0,1-j) 
			if(dp[cur][a][b][0][0]) 
				cmax(res[i+a][j+b],res[i][j]*dp[cur][a][b][0][0]);
	}
}

Pii Read(){
	static char O[5];
	scanf("%s",O);
	if(*O=='0') return mp(-1,0);
	if(isalpha(*O)) {
		if(*O=='E') return mp(3,0);
		if(*O=='S') return mp(3,1);
		if(*O=='W') return mp(3,2);
		if(*O=='N') return mp(3,3);
		if(*O=='Z') return mp(3,4);
		if(*O=='B') return mp(3,5);
		if(*O=='F') return mp(3,6);
		return mp(-1,-1);
	}
	if(O[1]=='m') return mp(0,*O-'1');
	if(O[1]=='p') return mp(1,*O-'1');
	if(O[1]=='s') return mp(2,*O-'1');
	return mp(-1,-1);
}

void Solve(){
	ans=0,memset(val,0,sizeof val),memset(mk,0,sizeof mk),val[0][0]=1;
	rep(i,0,2) rep(j,0,8) c[i][j]=4;
	rep(i,0,6) c[3][i]=4;
	while(1) {
		Pii T=Read();
		if(T.first==-1) break;
		c[T.first][T.second]--;
	}
	while(1) {
		Pii T=Read();
		if(T.first==-1) break;
		mk[T.first][T.second]=1;
	}
	Check7(),Check13();
	rep(i,0,2) Work(c[i],mk[i],val);
	rep(i,0,6) {
		static ll w[5];
		int x=c[3][i];
		rep(j,0,x) w[j]=C[x][j]<<(j*mk[3][i]);
		drep(a,4,0) drep(b,1,0) if(val[a][b]) {
			if(b<1 && x>=2) cmax(val[a][b+1],val[a][b]*w[2]); // 雀
			if(a<4 && x>=3) cmax(val[a+1][b],val[a][b]*w[3]); // 刻
			if(a<4 && x>=4) cmax(val[a+1][b],val[a][b]*w[4]); // 杠
		}
	}
	cmax(ans,val[4][1]);
	printf("%lld\n",ans);
}

int main(){
	rep(i,0,4) rep(j,C[i][0]=1,i) C[i][j]=C[i-1][j]+C[i-1][j-1];
	int T; scanf("%d",&T);
	while(T--) Solve();
}
