        if(opt==2) Insert();
        else if(opt==3) val[0]=rd<ll>()-1,Erase();
        else {
            ll now=rd<ll>(),des=rd<ll>(),ans=cnt=0;
            while(now<des) {
                val[n+1]=now;
                Pii x=Split2(rt,n+1);
                ll nxt=x.second?val[mi[x.second]]+1:1e18;
